example.code:
# Get OAuth2 token
curl -X POST https://api.c2m.com/auth/tokens/long \
  -H "Content-Type: application/json" \
  -d '{"grant_type": "client_credentials", "client_id": "YOUR_CLIENT_ID", "client_secret": "YOUR_SECRET"}'

# Submit single document
curl -X POST https://api.c2m.com/jobs/submit/single/doc \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "document": {
      "url": "https://example.com/letter.pdf"
    },
    "recipient": {
      "name": "John Doe",
      "address1": "123 Main St",
      "city": "Anytown",
      "state": "CA",
      "zip": "12345"
    }
  }'

# Get OAuth2 token
curl -X POST https://api.c2m.com/auth/tokens/long \
  -H "Content-Type: application/json" \
  -d '{"grant_type": "client_credentials", "client_id": "YOUR_CLIENT_ID", "client_secret": "YOUR_SECRET"}'

# Submit with template
curl -X POST https://api.c2m.com/jobs/submit/single/doc/template \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "templateId": "legal-letter-template-v2",
    "document": {
      "url": "https://example.com/client-letter.pdf"
    },
    "recipientOverride": {
      "name": "Jane Smith",
      "address1": "456 Oak Ave"
    }
  }'

# Get OAuth2 token
curl -X POST https://api.c2m.com/auth/tokens/long \
  -H "Content-Type: application/json" \
  -d '{"grant_type": "client_credentials", "client_id": "YOUR_CLIENT_ID", "client_secret": "YOUR_SECRET"}'

# Submit multiple documents
curl -X POST https://api.c2m.com/jobs/submit/multi/doc \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "documents": [
      {
        "url": "https://example.com/invoice-001.pdf",
        "addressCapture": true
      },
      {
        "url": "https://example.com/invoice-002.pdf",
        "addressCapture": true
      }
    ],
    "jobOptions": {
      "color": true,
      "duplex": false
    }
  }'

# Get OAuth2 token
curl -X POST https://api.c2m.com/auth/tokens/long \
  -H "Content-Type: application/json" \
  -d '{"grant_type": "client_credentials", "client_id": "YOUR_CLIENT_ID", "client_secret": "YOUR_SECRET"}'

# Batch submit
curl -X POST https://api.c2m.com/jobs/submit/multi/doc/template \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "templateId": "monthly-statement-template",
    "documents": [
      {"url": "https://example.com/statements/batch1.zip"}
    ],
    "processingOptions": {
      "unzipFiles": true,
      "validateAddresses": true
    }
  }'

# Get OAuth2 token
curl -X POST https://api.c2m.com/auth/tokens/long \
  -H "Content-Type: application/json" \
  -d '{"grant_type": "client_credentials", "client_id": "YOUR_CLIENT_ID", "client_secret": "YOUR_SECRET"}'

# Submit and merge
curl -X POST https://api.c2m.com/jobs/submit/multi/doc/merge \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "primaryDocument": {
      "url": "https://example.com/patient-report.pdf"
    },
    "attachments": [
      {"url": "https://example.com/general-health-info.pdf"},
      {"url": "https://example.com/medication-guide.pdf"}
    ],
    "recipient": {
      "name": "Patient Name",
      "address1": "789 Health St",
      "city": "Medical City",
      "state": "TX",
      "zip": "75001"
    }
  }'

# Get OAuth2 token
curl -X POST https://api.c2m.com/auth/tokens/long \
  -H "Content-Type: application/json" \
  -d '{"grant_type": "client_credentials", "client_id": "YOUR_CLIENT_ID", "client_secret": "YOUR_SECRET"}'

# Submit PDF for splitting
curl -X POST https://api.c2m.com/jobs/submit/pdf/split \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "document": {
      "url": "https://example.com/combined-invoices.pdf"
    },
    "splitOptions": {
      "delimiter": "pageBreak",
      "addressCapture": true,
      "addressLocation": "topRight"
    }
  }'
